import { useState } from "react";

function PhotographySection() {
  const photos = [
    {
      src: "https://drive.google.com/file/d/1imHV5oYj_Uzeof5aU6P-Wgt9sWpOXZHy/view?usp=drive_link",
      alt: "Nature 1",
      caption: "Golden Hour in the Fields",
      category: "Nature",
    },
    {
      src: "https://picsum.photos/id/1016/400/300",
      alt: "Nature 2",
      caption: "Misty Morning",
      category: "Nature",
    },
    {
      src: "https://picsum.photos/id/1024/400/300",
      alt: "Urban 1",
      caption: "City Life in Motion",
      category: "Urban",
    },
    {
      src: "https://picsum.photos/id/1027/400/300",
      alt: "Urban 2",
      caption: "Old Streets, New Stories",
      category: "Urban",
    },
    {
      src: "https://picsum.photos/id/1035/400/300",
      alt: "Portrait",
      caption: "Portrait of Grace",
      category: "People",
    },
    {
      src: "https://picsum.photos/id/1039/400/300",
      alt: "Landscape",
      caption: "Wide Horizons",
      category: "Nature",
    },
  ];

  // Manage which captions are visible with an array of booleans
  const [visibleCaptions, setVisibleCaptions] = useState(
    Array(photos.length).fill(false)
  );

  const toggleCaption = (index) => {
    setVisibleCaptions((prev) => {
      const newVisibility = [...prev];
      newVisibility[index] = !newVisibility[index];
      return newVisibility;
    });
  };

  return (
    <section className="section photography">
      <h2>Photography</h2>
      <p>In this section, you'll discover a curated selection of my photography work. Every moment is worth capturing because stories can be shared through the lens.
        </p>
      <div className="photo-grid">
        {photos.map(({ src, alt, caption }, i) => (
          <figure key={i} className="photo-card">
            <img
              src={src}
              alt={alt}
              onClick={() => toggleCaption(i)}
              style={{ cursor: "pointer" }}
            />
            {visibleCaptions[i] && (
              <figcaption>
                <p>{caption}</p>
              </figcaption>
            )}
          </figure>
        ))}
      </div>
    </section>
  );
}

export default PhotographySection;
